$.exports = function(rule, web, url, html) {
    let d = [];

    let alist = pdfa(html, "body&&a");
    let arr = alist.map(it => {
        return {
            //html: it,
            text: pdfh(it, "a&&Text"),
            title: pdfh(it, "a&&title"),
            href: pd(it, "a&&href", url)
        }
    });
    //log(arr);
    let debug = false;

    function clearText(it) {
        return it.replace(/第|集|章/g, "");
    }

    function isMovie(it) {
        if (it == null || it.text == null) {
            return false;
        }
        let tit = it.title || "";
        it = it.text || "";
        if (it == "" || it.length > 8) {
            return false;
        }
        //排除
        let reg = /\.|高清直播|写真推荐|影院|影视|蓝光电影|高清电影|第一季|第二季|第三季|第四季|第五季/;
        if (tit != "" && !tit.includes(it) || reg.test(it)) {
            return false;
        }
        return it.match(/原画|备用|蓝光|超清|高清|正片|韩版|4K|4k|1080P|720P|TC|HD|BD/)
    }

    function notChapter(it) {
        if (it == null || it.text == null) {
            return true;
        }
        return it.text.match(/[0-9]\.[0-9]分/);
    }

    function isChapter(it, pre, next) {
        if (notChapter(it)) {
            //优先排除
            return false;
        }
        //判断是不是电影
        if (isMovie(it)) {
            return true;
        }
        return isChapter0(it, pre) || isChapter0(it, next);
    }

    function getChapterNum(it) {
        if (it == null || it.text == null) {
            return -1;
        }
        it = it.text || "";
        if (it == "") {
            return -1;
        }
        it = clearText(it);
        let reg = /^[0-9]*$/;
        if (!reg.test(it)) {
            return -1;
        }
        it = parseInt(it);
        if (isNaN(it)) {
            return -1;
        }
        if (it > 1900 && it < 2100) {
            return -1;
        }
        return it;
    }

    function isChapter0(it, brother) {
        /*if (debug) {
            log({
                it: it,
                brother: brother
            });
        }*/
        it = getChapterNum(it);
        //if (debug) log(it);
        if (it < 0) {
            return false;
        }
        brother = getChapterNum(brother);
        //if (debug) log(brother);
        if (brother < 0) {
            return false;
        }
        return it - brother < 2 && it - brother > -2;
    }

    let _web = $.toString(() => {
        let urls = _getUrls();
        let reg = /\.html|\.css|\.js/;
        for (let k in urls) {
            if (!reg.test(urls[k]) && urls[k].match(/\.mp4|\.m3u8/)) {
                fy_bridge_app.log(urls[k]);
                return fy_bridge_app.getHeaderUrl(urls[k].replace(/.*?url=/, "")) + "#ignoreImg=true#";
            }
        }
    });

    for (let i = 0; i < arr.length; i++) {
        let it = arr[i];
        let t = it.text;
        if (!it.href || it.href == "") {
            continue;
        }
        let pre = i == 0 ? null : arr[i - 1];
        let next = i == (arr.length - 1) ? null : arr[i + 1];
        if (isChapter(it, pre, next)) {
            if (web) {
                var urlx = "webRule://" + it.href + "@" + _web;
                var extrax = {
                    jsLoadingInject: true,
                    id: it.href,
                    blockRules: ['.m4a', '.mp3', '.flv', '.avi', '.3gp', '.mpeg', '.wmv', '.mov', '.rmvb', '.gif', '.jpeg', '.png', '.ico', '.svg']
                };
            } else {
                var urlx = "video://" + it.href;
                var extrax = {
                    id: it.href
                };
            }
            d.push({
                title: t,
                url: urlx,
                col_type: "text_3",
                extra: extrax
            });
        }
    }
    if (d.length == 0) {
        return d;
    } else {
        d.splice(0, 0, {
            title: "““””<small><font color='#aaaaaa'>🔗" + url + "</font></small>",
            url: "hiker://page/ysfx?rule=" + rule + "&r=" + rule + "##" + url,
            col_type: "text_1",
            extra: {
                lineVisible: false
            }
        });
        //为线路加分割线
        let d2 = [];
        for (let i = 0; i < d.length; i++) {
            d2.push(d[i]);
            if (i < d.length - 1) {
                let it = d[i];
                let t1 = parseInt(clearText(it.title));
                let next = d[i + 1];
                let t2 = parseInt(clearText(next.title));
                if (t2 - t1 > 1 || t1 - t2 > 1) {
                    d2.push({
                        col_type: "big_blank_block"
                    });
                    d2.push({
                        col_type: "line_blank"
                    });
                    d2.push({
                        col_type: "big_blank_block"
                    });
                }
            }
        }       
        d2.push({
            title: "<small><font color='#aaaaaa'>匹配有误？点我不再匹配此页面</font></small>",
            url: $("#noLoading#").lazyRule((url) => {
                confirm({
                    title: "温馨提示",
                    content: "如果只是单次匹配失败，点击下方取消按钮刷新页面重新匹配，如果确认无法匹配，点击下方确定按钮加入白名单",
                    cancel: "refreshPage()",
                    confirm: $.toString((url) => {
                        let list = storage0.getItem("white", []);
                        list.push(url);
                        storage0.setItem("white", list);
                        refreshPage();
                        return "toast://已加入白名单"
                    }, url)
                });
                return "hiker://empty"
            }, url),
            col_type: "avatar",
            img: "hiker://images/account"
        });
        return d2;
    }
}

